package com.syntax.class03;

public class task1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x=10;
		int y=500;	
		x+=y;
		System.out.println(x); //510
		int z=200;
		x+=z;
		System.out.println(x); //710
		
		int w=60;
		x-=w;
		System.out.println(x); //650
		
		int cakePiece=11;
		cakePiece/=4;
		System.out.println(cakePiece); //2
		int cake=25;
		System.out.println(cake/7);//3
		cake%=7;
		System.out.println(cake); //4
		
		
		
	}

}
